import React, {createContext} from 'react'
import axios from 'axios';
const { Provider , Consumer} = createContext(null);


function LoginContextProvider(props){
        const [userDetails,setUserDetails]=React.useState(getIsLoggedIn
                );

            console.log("context: "+userDetails.isLoggedIn);
        return (
                <Provider value={{
                        userDetails ,
                        setUserDetails
                }}>
                        {props.children}
                </Provider>
        );
}

const getIsLoggedIn = async () => {
        const request = {
                method: 'post',
                url:"http://localhost:4000/login",
                headers: {'Content-Type': 'application/json'}, 
                withCredentials:true
        }
        const val = await axios(request)
                .then(response => response.data)
        return val ? val : {isLoggedIn:false};
}

export { LoginContextProvider , Consumer as LoginContextConsumer}
